sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        title,
        tooltipPreview,
        primaryItem,
        secondaryItems,
        hasUpdate,
        isDirty,
        status,
        screenParams
    } = event.payload;

    helpers.screen.updateStatus({
        title,
        tooltipPreview,
        primaryItem,
        secondaryItems,
        hasUpdate,
        isDirty,
        status,
        screenParams: {
            table: screenParams && screenParams.table,
            sysId: screenParams && screenParams.sys_id
        }
    });
}
